mapper.select:
SELECT * FROM post_education where isdelete = '0'
and title like concat('%',#{title},'%')
and date(create_time) = #{startTime}
order by update_time desc
order by create_time desc
limit #{start},#{end}

SELECT
*
FROM post_education where isdelete = '0'


  and name = #{name}


  and type = #{type}


  and son_type = #{sonType}

SELECT count(1) FROM post_education where isdelete = '0'
and title like concat('%',#{title},'%')
and create_time >= #{startTime}
and create_time <= #{endTime}
order by update_time desc
order by create_time desc

SELECT
(@rowno:=@rowno+1) AS rowNum,a.*
FROM post_education a, (select @rowno:=0+ #{start}) b where isdelete = '0'

  and title like concat('%',#{title},'%')


  and create_time  >=  #{startTime}


  and create_time  <=  #{endTime}


  order by update_time desc


  order by create_time desc

limit #{start},#{end}
