mapper.select:
select (@rowno:=@rowno+1) AS rowNum,a.* from post_information_manage a,(select @rowno:=0+ #{currentPage}) b where 1=1 and data_state = 1
and information_type like concat('%',#{informationType},'%')
and date(create_time) = #{createTime}
order by update_time desc
order by create_time desc
limit #{currentPage},#{size}

select a.id,a.datum_ids as datumIds,a.information_theme as informationTheme,a.information_content as informationContent,a.information_type as informationType, (@rowno:=@rowno+1) AS rowNum from post_information_manage a,(select @rowno:=0+ #{start})b where a.data_state = 1
and information_type = #{informationType}
order by a.update_time desc limit #{start},1
