mapper.select:
select a.id, a.datum_ids,a.collection_amount, a.process_state,a.process_result, a.user_id,a.theme_name, b.phone as user_name from post_theme_show a left join post_pub_user b on a.user_id = b.id where 1=1 and a.data_state = 1 and a.theme_source = '2'
and a.theme_name like concat('%',#{themeName},'%')
and a.process_state = #{processState}
and a.process_result = #{processResult}
order by update_date desc limit #{currentPage},#{size}

select count(*) from post_theme_show a left join post_pub_user b on a.user_id = b.id where 1=1 and data_state = 1 and theme_source = '2'
and a.theme_name like concat('%',#{themeName},'%')
and process_state = #{processState}
and process_result = #{processResult}
order by update_date desc

select count(1)
from post_theme_show where user_id = #{userId}

  and process_result = #{status}

and data_state = 1 and theme_source = '2'

select id, theme_name themeName, theme_describe themeContent, datum_ids datumIds, process_state processState, process_result processResult, create_date createTime from post_theme_show where user_id = #{userId}
and process_result = #{status}
and data_state = 1 and theme_source = '2' order by create_date desc
limit #{start},#{size}
