mapper.select:
select

from  post_stamp_story
where 1=1  and  data_state = 1

  and story_type = #{storyType}


  and  date(create_time) = #{createTime}


  order by update_time desc


  order by create_time desc

limit #{currentPage},#{size}

select count(*) from  post_stamp_story
where 1=1 and  data_state = 1

  and story_type = #{storyType}


  and  date(create_time) = #{createTime}


  order by update_time desc


  order by create_time desc

select
(@rowno:=@rowno+1) AS rowNum,a.*
from  post_stamp_story a, (select @rowno:=0+ #{currentPage}) b
where 1=1  and  data_state = 1

  and story_type= #{storyType}


  and  date(create_time) = #{createTime}

order by update_time  desc
limit #{currentPage},#{size}

select count(*) from  post_stamp_story
where 1=1 and  data_state = 1

  and story_type = #{storyType}


  and  date(create_time) = #{createTime}

order by update_time  desc

select a.id,a.story_type as storyType,a.story_title as storyTitle, a.story_content as storyContent,a.story_type as storyType, a.create_time as createTime, (@rowno:=@rowno+1) AS rowNum from post_stamp_story a,(select @rowno:=0+ #{start})b where a.data_state = 1
and story_type = #{storyType}
order by a.update_time desc limit #{start},1
