mapper.select:
SELECT
count(1)
FROM post_pub_user where isdelete = 0

  and (user_name like concat('%',#{name},'%')
  and user_name like concat('%',#{name},'%')
  )


  and phone = #{phone}


  and start_time = #{startTime}


  order by update_time desc


  order by create_time desc

SELECT a.*,b.name as city FROM post_pub_user a left join mip_area b on a.city_id = b.id where isdelete = 0
and (user_name like concat('%',#{name},'%') and user_name like concat('%',#{name},'%') )
and phone = #{phone}
and date(create_time) = #{createTime}
order by update_time desc
order by create_time desc
limit #{start},#{end}

SELECT
*
FROM post_pub_user where isdelete = 0


  and name = #{name}


  and type = #{type}


  and son_type = #{sonType}
